from typing import List
class Solution:
    # 超时
    def threeSum1(self, nums: List[int]) -> List[List[int]]:
        length = len(nums)
        if length == 3:
            return []
        else:
            combinations  = self.combination(length, 3)
            result =[[nums[combination[0]], nums[combination[1]], nums[combination[2]]] for combination in combinations if sum([nums[combination[0]], nums[combination[1]], nums[combination[2]]]) == 0]
            ret = []
            for res in result:
                tmp = [min(res), sum(res) - min(res) - max(res), max(res)]
                if tmp not in ret:
                    ret.append(tmp)
            return ret
            
    def combination(self, n, k):
        if k == 1:
            return [[i] for i in range(n)]
        elif k == n:
            return [[i for i in range(n)]]

        answer = self.combination(n - 1, k)
        for item in self.combination(n - 1, k - 1):
            item.append(n - 1)
            answer.append(item)
        return answer
        
    # 1.将数组排序 
    # 2.定义三个指针，i，j，k。遍历i，
    # 那么这个问题就可以转化为在i之后的数组中寻找nums[j]+nums[k]=-nums[i]这个问题，
    # 也就将三数之和问题转变为二数之和---（可以使用双指针）
    def threeSum(self, nums: List[int])->List[List[int]]:
        
        answer = {}
        length = len(nums)
        if length < 3:
            return []
        if len(set(nums)) == 1:
            
            return [[nums[0], nums[0], nums[0]]] if nums[0] == 0 else []
        nums.sort()
        for index, num in enumerate(nums):
            i, j = 0, length - 1
            while i < j:
                if i == index or nums[i] + nums[j] + num < 0:
                    i += 1
                elif j == index or nums[i] + nums[j] + num > 0:
                    j -= 1
                elif nums[i] + nums[j] + num == 0:
                      
                    tmp = [nums[i], nums[j], num]
                    tmp = (min(tmp), sum(tmp) - min(tmp) - max(tmp), max(tmp))
                    i += 1
                    j -= 1
                    answer[tmp] = 0
                
                   
        return [[key[0], key[1], key[2]] for key in answer.keys()]
    # 时间很快
    def threeSum(self, nums):
        ans = []
        n = len(nums)
        nums.sort()
        for i in range(n):
            left = i + 1
            right = n - 1
            if nums[i] > 0:
                break
            if i >= 1 and nums[i] == nums[i - 1]:
                continue
            while left < right:
                total = nums[i] + nums[left] + nums[right]
                if total > 0:
                    right -= 1
                elif total < 0:
                    left += 1
                else:
                    ans.append([nums[i], nums[left], nums[right]])
                    while left != right and nums[left] == nums[left + 1]: left += 1
                    while left != right and nums[right] == nums[right - 1]: right -= 1
                    left += 1
                    right -= 1
        return ans

